const { app, BrowserWindow, Tray, Menu, nativeImage } = require('electron');
const path = require('path');
const fs = require('fs');

let mainWindow = null;
let tray = null;

function createWindow() {
    mainWindow = new BrowserWindow({
        width: 640,
        height: 480,
        show: !process.argv.includes('--start-minimized'),
        webPreferences: {
            nodeIntegration: true,
            contextIsolation: false
        }
    });

    mainWindow.loadURL('https://music.yandex.kz/', {
        userAgent: 'Mozilla/5.0 (X11; Linux x86_64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36'
    });

    Menu.setApplicationMenu(null);

    mainWindow.on('close', (event) => {
        if (!app.isQuitting) {
            event.preventDefault();
            mainWindow.hide();
        }
    });

}

function createTray() {
    let icon;

    const iconPaths = [
        path.join(__dirname, 'icon.png'),
        path.join(__dirname, 'assets', 'icon.png'),
        path.join(__dirname, 'resources', 'icon.png'),
        path.join(process.resourcesPath, 'icon.png')
    ];
    
    let iconPath = null;
    for (const currentPath of iconPaths) {
        if (fs.existsSync(currentPath)) {
            iconPath = currentPath;
            break;
        }
    }
    
    if (iconPath) {
        icon = nativeImage.createFromPath(iconPath);
    } else {
        icon = nativeImage.createFromDataURL('data:image/png;base64,');
    }
    
    if (icon.isEmpty()) {
        return;
    }

    tray = new Tray(icon);

    const contextMenu = Menu.buildFromTemplate([
        {
            label: 'Восстановить',
            click: () => {
                mainWindow.show();
            }
        },
        {
            label: 'Выход',
            click: () => {
                app.isQuitting = true;
                app.quit();
            }
        }
    ]);

    tray.setToolTip('Yandex Music');
    tray.setContextMenu(contextMenu);

    tray.on('click', () => {
        mainWindow.show();
    });

    // Дополнительная обработка для двойного клика
    tray.on('double-click', () => {
        mainWindow.show();
    });
}

app.whenReady().then(() => {
    createWindow();
    createTray();

    app.on('activate', () => {
        if (BrowserWindow.getAllWindows().length === 0) createWindow();
    });
});

app.on('window-all-closed', () => {
    if (process.platform !== 'darwin') {
        return;
    }
    app.quit();
});

app.on('before-quit', () => {
    app.isQuitting = true;
});